// Extract narration script from Groq response
const groqOutput = $input.item.json;
let script = '';

if (groqOutput.message?.content) {
  script = groqOutput.message.content;
} else if (groqOutput.text) {
  script = groqOutput.text;
} else if (typeof groqOutput === 'string') {
  script = groqOutput;
} else {
  // Fallback
  const verse = $('Webhook').first().json.body.verse;
  const reference = $('Webhook').first().json.body.reference;
  const speaker = $('Webhook').first().json.body.speaker;
  script = `${speaker} says in ${reference}: "${verse}". May this promise bring you hope today.`;
}

return {
  script: script.trim(),
  reference: $('Webhook').first().json.body.reference
};
