// Select voice based on promise ID (rotate through 4 voices)
const promiseId = $('Extract Promise Data').item.json.promiseId;
const script = $input.first().json.message?.content || $input.first().json.text || $input.first().json.output;

// Azure Neural Voices - Multilingual, natural sounding
const voices = [
  'en-US-AndrewMultilingualNeural',  // Male 1: Warm, trustworthy
  'en-US-AvaMultilingualNeural',     // Female 1: Clear, gentle
  'en-US-EricNeural',                // Male 2: Professional, calm
  'en-US-JennyNeural'                // Female 2: Friendly, expressive
];

// Use promise ID to determine which voice (cycles through 4 voices)
const voiceIndex = promiseId % 4;
const selectedVoice = voices[voiceIndex];

return {
  script: script,
  voice: selectedVoice,
  promiseId: promiseId,
  reference: $('Extract Promise Data').item.json.reference
};

const promiseId = $('Select Voice').item.json.promiseId;
const reference = $('Select Voice').item.json.reference;
const script = $('Select Voice').item.json.script;

const audioUrl = `/audio/promises/${promiseId}.mp3`;

return {
  success: true,
  audioUrl: audioUrl,
  script: script,
  reference: reference,
  timestamp: new Date().toISOString()
};
