// Extract trivia from Groq response
const groqOutput = $input.item.json;
let trivia = '';

if (groqOutput.message?.content) {
  trivia = groqOutput.message.content;
} else if (groqOutput.text) {
  trivia = groqOutput.text;
} else if (typeof groqOutput === 'string') {
  trivia = groqOutput;
} else {
  // Fallback
  const name = $('Webhook').first().json.body.name;
  const books = $('Webhook').first().json.body.books.join(', ');
  const famousFor = $('Webhook').first().json.body.famousFor;
  trivia = `${name} is mentioned in ${books}. ${famousFor}`;
}

return {
  trivia: trivia.trim()
};
